export const GET_CURRENT_USER = 'GET_CURRENT_USER';


export const getCurrentUser = () => dispatch => {
  dispatch({
    type: GET_CURRENT_USER,
    payload: {
      id: 5,
      fn: 'Andrey Ilchenko',
      username: 'Admin',
      phone: '+380681111111',
      email: 'andrey_ilchenko137@ukr.net',
    },
  })
}


const initialState = {};


export default (state = initialState, action) => {
  switch (action.type) {
    case GET_CURRENT_USER: {
      return {
        ...state,
        ...action.payload,
      }
    }
    default:
      return state;
  }
}
